// SAFE: Load the key from the environment

// --- Main Entry Point ---
void main() async {
  WidgetsFlutterBinding.ensureInitialized();
  
  // Load the .env file
  await dotenv.load(fileName: ".env");

  // *** THIS IS THE FIX ***
  // We check if Firebase has ALREADY been initialized.
  // This prevents the "[core/duplicate-app]" crash on macOS and iOS.
  if (Firebase.apps.isEmpty) {
    await Firebase.initializeApp(
      options: DefaultFirebaseOptions.currentPlatform,
    );
  }
  runApp(const MyApp());
} //

