// --- 2. Auth Wrapper ---
class AuthWrapper extends StatelessWidget {
  const AuthWrapper({super.key});

  @override
  Widget build(BuildContext context) {
    // This builder automatically listens for login/logout events
    return StreamBuilder<User?>(
      stream: FirebaseAuth.instance.authStateChanges(),
      builder: (context, snapshot) {

        // 1. If still connecting, show loading screen
        if (snapshot.connectionState == ConnectionState.waiting) {
          return const LoadingScreen(message: 'Connecting...');
        }

        // 2. If there's an error
        if (snapshot.hasError) {
          return ErrorScreen(message: 'Authentication error: ${snapshot.error}');
        }

        // 3. If we have data, check if user is null or not
        if (snapshot.hasData) {
          // User is logged IN
          return ProfileCheckWrapper(user: snapshot.data!);
        } else {
          // User is logged OUT
          return const AuthScreen();
        }
      },
    );
  }
}

// --- 3. Profile Check Wrapper ---
class ProfileCheckWrapper extends StatefulWidget {
  final User user;
  const ProfileCheckWrapper({super.key, required this.user});

  @override
  State<ProfileCheckWrapper> createState() => _ProfileCheckWrapperState();
}

class _ProfileCheckWrapperState extends State<ProfileCheckWrapper> {
  late final Future<DocumentSnapshot> _profileCheck;

  @override
  void initState() {
    super.initState();
    _profileCheck = FirebaseFirestore.instance
        .collection('artifacts')
        .doc(appId)
        .collection('users')
        .doc(widget.user.uid)
        .get();
  }

  @override
  Widget build(BuildContext context) {
    return FutureBuilder<DocumentSnapshot>(
      future: _profileCheck,
      builder: (context, snapshot) {
        if (snapshot.connectionState == ConnectionState.waiting) {
          return const LoadingScreen(message: 'Loading profile...');
        }

        if (snapshot.hasError) {
          print('--- PROFILE CHECK FAILED ---');
          print(snapshot.error);
          print('-----------------------------');
          return ErrorScreen(
              message:
                  'Could not load profile. This is likely a database rules issue.\n\nError: ${snapshot.error}');
        }

        if (snapshot.data != null && snapshot.data!.exists) {
          final userData = snapshot.data!.data() as Map<String, dynamic>;
          return HomeScreen(user: widget.user, userData: userData);
        } else {
          return OnboardingScreen(user: widget.user);
        }
      },
    );
  }
}

