class AuthScreen extends StatefulWidget {
  const AuthScreen({super.key});

  @override
  State<AuthScreen> createState() => _AuthScreenState();
}

class _AuthScreenState extends State<AuthScreen> {
  bool _isLogin = true;
  bool _isLoading = false;
  String _errorMessage = '';
  final _formKey = GlobalKey<FormState>();

  final _emailController = TextEditingController();
  final _passwordController = TextEditingController();

  @override
  void dispose() {
    _emailController.dispose();
    _passwordController.dispose();
    super.dispose();
  }

  Future<void> _handleSubmit() async {
    if (!_formKey.currentState!.validate()) {
      return;
    }

    setState(() {
      _isLoading = true;
      _errorMessage = '';
    });

    try {
      if (_isLogin) {
        await FirebaseAuth.instance.signInWithEmailAndPassword(
          email: _emailController.text.trim(),
          password: _passwordController.text.trim(),
        );
      } else {
        await FirebaseAuth.instance.createUserWithEmailAndPassword(
          email: _emailController.text.trim(),
          password: _passwordController.text.trim(),
        );
      }
    } on FirebaseAuthException catch (e) {
      print('--- AUTH FAILED ---');
      print(e);
      print('-------------------');
      setState(() {
        _errorMessage = e.message ?? 'An unknown authentication error occurred.';
      });
    } catch (e) {
      print('--- UNKNOWN AUTH/SIGNUP FAILED ---');
      print(e);
      print('------------------------------------');
      setState(() {
        _errorMessage =
            'An unexpected error occurred. Please try again.\n${e.toString()}';
      });
    } finally {
      if (mounted) {
        setState(() {
          _isLoading = false;
        });
      }
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: Center(
          child: SingleChildScrollView(
            child: Padding(
              padding: const EdgeInsets.all(24.0),
              child: Form(
                key: _formKey,
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  crossAxisAlignment: CrossAxisAlignment.stretch,
                  children: [
                    Row(
                      mainAxisAlignment: MainAxisAlignment.center,
                      children: [
                        Icon(LucideIcons.heartPulse,
                            color: Colors.pink[400], size: 48),
                        const SizedBox(width: 12),
                        Icon(LucideIcons.baby,
                            color: Colors.blue[400], size: 48),
                      ],
                    ),
                    const SizedBox(height: 24),
                    Text(
                      _isLogin ? 'Welcome Back' : 'Create Account',
                      textAlign: TextAlign.center,
                      style: Theme.of(context)
                          .textTheme
                          .headlineMedium
                          ?.copyWith(fontWeight: FontWeight.bold),
                    ),
                    const SizedBox(height: 8),
                    Text(
                      'Your partner in maternal health.',
                      textAlign: TextAlign.center,
                      style: Theme.of(context).textTheme.titleMedium,
                    ),
                    const SizedBox(height: 40),
                    TextFormField(
                      controller: _emailController,
                      decoration: const InputDecoration(
                        labelText: 'Email',
                        prefixIcon: Icon(LucideIcons.mail),
                      ),
                      keyboardType: TextInputType.emailAddress,
                      validator: (value) {
                        if (value == null ||
                            value.trim().isEmpty ||
                            !value.contains('@')) {
                          return 'Please enter a valid email.';
                        }
                        return null;
                      },
                    ),
                    const SizedBox(height: 16),
                    TextFormField(
                      controller: _passwordController,
                      decoration: const InputDecoration(
                        labelText: 'Password',
                        prefixIcon: Icon(LucideIcons.lock),
                      ),
                      obscureText: true,
                      validator: (value) {
                        if (value == null || value.trim().length < 6) {
                          return 'Password must be at least 6 characters.';
                        }
                        return null;
                      },
                    ),
                    const SizedBox(height: 24),
                    if (_errorMessage.isNotEmpty)
                      Padding(
                        padding: const EdgeInsets.only(bottom: 16.0),
                        child: Text(
                          _errorMessage,
                          style: const TextStyle(color: Colors.red),
                          textAlign: TextAlign.center,
                        ),
                      ),
                    ElevatedButton(
                      onPressed: _isLoading ? null : _handleSubmit,
                      child: _isLoading
                          ? const SizedBox(
                              height: 24,
                              width: 24,
                              child: CircularProgressIndicator(
                                strokeWidth: 2,
                                color: Colors.white,
                              ),
                            )
                          : Text(_isLogin ? 'Login' : 'Sign Up'),
                    ),
                    const SizedBox(height: 16),
                    TextButton(
                      onPressed: () {
                        setState(() {
                          _isLogin = !_isLogin;
                          _errorMessage = '';
                        });
                      },
                      child: Text(
                        _isLogin
                            ? 'Don\'t have an account? Sign Up'
                            : 'Already have an account? Login',
                      ),
                    ),
                  ],
                ),
              ),
            ),
          ),
        ),
      ),
    );
  }
}

class OnboardingScreen extends StatefulWidget {
  final User user;
  const OnboardingScreen({super.key, required this.user});

  @override
  State<OnboardingScreen> createState() => _OnboardingScreenState();
}

class _OnboardingScreenState extends State<OnboardingScreen> {
  final _formKey = GlobalKey<FormState>();
  bool _isLoading = false;
  String _errorMessage = '';

  final _nameController = TextEditingController();
  final _dueDateController = TextEditingController();
  final _historyController = TextEditingController();
  final _conditionsController = TextEditingController();
  final _allergiesController = TextEditingController();

  @override
  void dispose() {
    _nameController.dispose();
    _dueDateController.dispose();
    _historyController.dispose();
    _conditionsController.dispose();
    _allergiesController.dispose();
    super.dispose();
  }

  Future<void> _selectDate(BuildContext context) async {
    final DateTime? picked = await showDatePicker(
      context: context,
      initialDate: DateTime.now(),
      firstDate: DateTime.now().subtract(const Duration(days: 300)),
      lastDate: DateTime.now().add(const Duration(days: 300)),
    );
    if (picked != null) {
      setState(() {
        _dueDateController.text =
            "${picked.year}-${picked.month.toString().padLeft(2, '0')}-${picked.day.toString().padLeft(2, '0')}";
      });
    }
  }

  Future<void> _handleSaveProfile() async {
    if (!_formKey.currentState!.validate()) {
      return;
    }

    setState(() {
      _isLoading = true;
      _errorMessage = '';
    });

    final profileData = {
      'name': _nameController.text.trim(),
      'dueDate': _dueDateController.text.trim(),
      'medicalHistory': _historyController.text.trim(),
      'currentConditions': _conditionsController.text.trim(),
      'allergies': _allergiesController.text.trim(),
      'createdAt': FieldValue.serverTimestamp(),
    };

    try {
      final profileRef = FirebaseFirestore.instance
          .collection('artifacts')
          .doc(appId)
          .collection('users')
          .doc(widget.user.uid);

      await profileRef.set(profileData);

      if (mounted) {
        // This is a key fix: We use pushAndRemoveUntil to clear the nav stack
        // so the user can't go "back" to the onboarding screen.
        Navigator.of(context).pushAndRemoveUntil(
          MaterialPageRoute(builder: (context) => const AuthWrapper()),
          (Route<dynamic> route) => false,
        );
      }
    } catch (e) {
      print('--- PROFILE SAVE FAILED ---');
      print(e);
      print('---------------------------');
      if (mounted) {
        setState(() {
          _errorMessage =
              'Failed to save profile. Please try again.\n${e.toString()}';
          _isLoading = false;
        });
      }
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Welcome!'),
      ),
      body: Form(
        key: _formKey,
        child: ListView(
          padding: const EdgeInsets.all(16.0),
          children: [
            Text(
              "Let's set up your profile.",
              style: Theme.of(context)
                  .textTheme
                  .headlineSmall
                  ?.copyWith(fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 8),
            const Text(
              "This information will help us personalize your experience.",
              style: TextStyle(color: Colors.grey),
            ),
            const SizedBox(height: 24),
            TextFormField(
              controller: _nameController,
              decoration: const InputDecoration(
                labelText: 'Full Name',
                prefixIcon: Icon(LucideIcons.user),
              ),
              validator: (value) {
                if (value == null || value.trim().isEmpty) {
                  return 'Please enter your name.';
                }
                return null;
              },
            ),
            const SizedBox(height: 16),
            TextFormField(
              controller: _dueDateController,
              decoration: const InputDecoration(
                labelText: 'Estimated Due Date',
                prefixIcon: Icon(LucideIcons.calendar),
              ),
              readOnly: true,
              onTap: () => _selectDate(context),
            ),
            const SizedBox(height: 16),
            TextFormField(
              controller: _historyController,
              decoration: const InputDecoration(
                labelText: 'Brief Medical History',
                hintText: 'e.g., previous pregnancies, surgeries',
                alignLabelWithHint: true,
              ),
              maxLines: 3,
            ),
            const SizedBox(height: 16),
            TextFormField(
              controller: _conditionsController,
              decoration: const InputDecoration(
                labelText: 'Current Medical Conditions',
                hintText: 'e.g., gestational diabetes',
                alignLabelWithHint: true,
              ),
              maxLines: 3,
            ),
            const SizedBox(height: 16),
            TextFormField(
              controller: _allergiesController,
              decoration: const InputDecoration(
                labelText: 'Allergies',
                hintText: 'medication, food, etc.',
                alignLabelWithHint: true,
              ),
              maxLines: 2,
            ),
            const SizedBox(height: 24),
            if (_errorMessage.isNotEmpty)
              Padding(
                padding: const EdgeInsets.only(bottom: 16.0),
                child: Text(
                  _errorMessage,
                  style: const TextStyle(color: Colors.red),
                  textAlign: TextAlign.center,
                ),
              ),
            ElevatedButton(
              onPressed: _isLoading ? null : _handleSaveProfile,
              child: _isLoading
                  ? const SizedBox(
                      height: 24,
                      width: 24,
                      child: CircularProgressIndicator(
                        strokeWidth: 2,
                        color: Colors.white,
                      ),
                    )
                  : const Text('Save Profile'),
            ),
          ],
        ),
      ),
    );
  }
}

// --- 6. Page Content ---

// --- HomePage ---
class HomePageContent extends StatefulWidget {
  final User user;
  final Map<String, dynamic> userData;
  final Function(int) onNavigate; // Callback to change tabs

  const HomePageContent({
    super.key,
    required this.user,
    required this.userData,
    required this.onNavigate,
  });

  @override
  State<HomePageContent> createState() => _HomePageContentState();
}

class _HomePageContentState extends State<HomePageContent> {
  late final Stream<QuerySnapshot> _appointmentsStream;
  late final Stream<QuerySnapshot> _medicinesStream;

  @override
  void initState() {
    super.initState();

    // Stream for next upcoming appointment
    _appointmentsStream = FirebaseFirestore.instance
        .collection('artifacts')
        .doc(appId)
        .collection('users')
        .doc(widget.user.uid)
        .collection('appointments')
        .where('date', isGreaterThanOrEqualTo: Timestamp.now())
        .orderBy('date')
        .limit(1)
        .snapshots();

    // Stream for next pending medicine
    _medicinesStream = FirebaseFirestore.instance
        .collection('artifacts')
        .doc(appId)
        .collection('users')
        .doc(widget.user.uid)
        .collection('medicines')
        .where('isTaken', isEqualTo: false)
        .orderBy('createdAt')
        .limit(1)
        .snapshots();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Maternal Health Hub'),
        actions: [
          IconButton(
            icon: const Icon(LucideIcons.logOut),
            onPressed: () {
              FirebaseAuth.instance.signOut();
            },
          )
        ],
      ),
      body: ListView(
        padding: const EdgeInsets.all(16.0),
        children: [
          Text(
            'Welcome, ${widget.userData['name'] ?? 'User'}!',
            style: Theme.of(context).textTheme.headlineMedium?.copyWith(
                  fontWeight: FontWeight.bold,
                ),
          ),
          const SizedBox(height: 8),
          Text(
            "Here's your summary. Stay healthy!",
            style: Theme.of(context).textTheme.titleMedium?.copyWith(
                  color: Colors.grey[700],
                ),
          ),
          const SizedBox(height: 24),
          _buildNextAppointmentCard(),
          _buildNextMedicineCard(),
          _buildNutritionCard(context),
        ],
      ),
    );
  }

  // --- Dashboard Card Widgets ---

  // A generic card builder
  Widget _buildDashboardCard({
    required BuildContext context,
    required IconData icon,
    required Color iconColor,
    required String title,
    required Widget content,
    required VoidCallback onTap,
  }) {
    return Card(
      child: InkWell(
        onTap: onTap,
        borderRadius: BorderRadius.circular(12.0),
        child: Padding(
          padding: const EdgeInsets.all(16.0),
          child: Row(
            children: [
              Icon(icon, color: iconColor, size: 32),
              const SizedBox(width: 16),
              Expanded(
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Text(
                      title,
                      style: Theme.of(context).textTheme.titleLarge?.copyWith(
                            fontWeight: FontWeight.bold,
                          ),
                    ),
                    const SizedBox(height: 4),
                    content,
                  ],
                ),
              ),
              const Icon(LucideIcons.chevronRight, color: Colors.grey),
            ],
          ),
        ),
      ),
    );
  }

  // Card for next appointment
  Widget _buildNextAppointmentCard() {
    return StreamBuilder<QuerySnapshot>(
      stream: _appointmentsStream,
      builder: (context, snapshot) {
        Widget content;
        if (snapshot.connectionState == ConnectionState.waiting) {
          content = const Text('Loading...', style: TextStyle(color: Colors.grey));
        } else if (snapshot.hasError) {
          print("--- APPOINTMENT STREAM ERROR ---");
          print(snapshot.error);
          print("--------------------------------");
          content = const Text('Error loading data', style: TextStyle(color: Colors.red));
        } else if (!snapshot.hasData || snapshot.data!.docs.isEmpty) {
          content =
              const Text('No upcoming appointments', style: TextStyle(color: Colors.grey));
        } else {
          final doc = snapshot.data!.docs.first;
          final data = doc.data() as Map<String, dynamic>;
          final DateTime date = (data['date'] as Timestamp).toDate();
          content = Text(
            '${data['title']} on ${DateFormat.yMd().format(date)}',
            style: const TextStyle(fontWeight: FontWeight.bold),
          );
        }

        return _buildDashboardCard(
          context: context,
          icon: LucideIcons.calendar,
          iconColor: Colors.blueAccent,
          title: 'Next Appointment',
          content: content,
          onTap: () => widget.onNavigate(2), // Navigate to Appts tab
        );
      },
    );
  }

  // Card for next medicine
  Widget _buildNextMedicineCard() {
    return StreamBuilder<QuerySnapshot>(
      stream: _medicinesStream,
      builder: (context, snapshot) {
        Widget content;
        if (snapshot.connectionState == ConnectionState.waiting) {
          content = const Text('Loading...', style: TextStyle(color: Colors.grey));
        } else if (snapshot.hasError) {
          print("--- MEDICINE STREAM ERROR ---");
          print(snapshot.error);
          print("-------------------------------");
          content = const Text('Error loading data', style: TextStyle(color: Colors.red));
        } else if (!snapshot.hasData || snapshot.data!.docs.isEmpty) {
          content =
              const Text('All medicines taken!', style: TextStyle(color: Colors.grey));
        } else {
          final doc = snapshot.data!.docs.first;
          final data = doc.data() as Map<String, dynamic>;
          content = Text(
            '${data['name']} - ${data['time']}',
            style: const TextStyle(fontWeight: FontWeight.bold),
          );
        }

        return _buildDashboardCard(
          context: context,
          icon: LucideIcons.pill,
          iconColor: Colors.pink,
          title: 'Next Medicine',
          content: content,
          onTap: () => widget.onNavigate(1), // Navigate to Meds tab
