// --- MedicineScreen ---
class MedicineScreen extends StatefulWidget {
  final User user;
  const MedicineScreen({super.key, required this.user});

  @override
  State<MedicineScreen> createState() => _MedicineScreenState();
}

class _MedicineScreenState extends State<MedicineScreen> {
  late final CollectionReference _medicineRef;

  @override
  void initState() {
    super.initState();
    _medicineRef = FirebaseFirestore.instance
        .collection('artifacts')
        .doc(appId)
        .collection('users')
        .doc(widget.user.uid)
        .collection('medicines');
  }

  Future<void> _addMedicine(
      String name, String dosage, String time, BuildContext modalContext) async {
    if (name.isEmpty) return;
    try {
      await _medicineRef.add({
        'name': name,
        'dosage': dosage,
        'time': time,
        'isTaken': false,
        'createdAt': FieldValue.serverTimestamp(),
      });
      Navigator.of(modalContext).pop();
    } catch (e) {
      print("Error adding medicine: $e");
      if (mounted) {
        ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(content: Text('Failed to add medicine: ${e.toString()}')),
        );
      }
    }
  }

  Future<void> _toggleIsTaken(DocumentSnapshot doc) async {
    try {
      await doc.reference.update({'isTaken': !doc['isTaken']});
    } catch (e) {
      print("Error updating medicine: $e");
    }
  }
  // *** NEW ***
  // Function to delete a medicine
  Future<void> _deleteMedicine(DocumentSnapshot doc) async {
    try {
      await doc.reference.delete();
      if (mounted) {
         ScaffoldMessenger.of(context).showSnackBar(
          SnackBar(
            content: Text('${doc['name']} deleted.'),
            backgroundColor: Colors.red,
          ),
        );
      }
    } catch (e) {
      print("Error deleting medicine: $e");
    }
  }

  void _showAddMedicineSheet() {
    final nameController = TextEditingController();
    final dosageController = TextEditingController();
    
    final timeController = TextEditingController();
    TimeOfDay? selectedTime = TimeOfDay.now();

    showModalBottomSheet(
      context: context,
      isScrollControlled: true,
      builder: (modalContext) {
        return StatefulBuilder(
          builder: (BuildContext context, StateSetter setModalState) {
            // *** FIX ***
            // Wrap the Column in a SingleChildScrollView
            // and use the padding for viewInsets
            return Padding(
              padding: EdgeInsets.only(
                bottom: MediaQuery.of(context).viewInsets.bottom,
                top: 20,
                left: 20,
                right: 20,
              ),
              child: SingleChildScrollView(
                child: Column(
                  mainAxisSize: MainAxisSize.min,
                  crossAxisAlignment: CrossAxisAlignment.stretch,
                  children: [
                    Text(
                      'Add New Medicine',
                      style: Theme.of(context).textTheme.headlineSmall,
                      textAlign: TextAlign.center,
                    ),
                    const SizedBox(height: 24),
                    TextField(
                      controller: nameController,
                      decoration: const InputDecoration(labelText: 'Medicine Name'),
                    ),
                    const SizedBox(height: 16),
                    TextField(
                      controller: dosageController,
                      decoration: const InputDecoration(labelText: 'Dosage (e.g., 1 pill)'),
                    ),
                    const SizedBox(height: 16),
                    TextFormField(
                      controller: timeController,
                      readOnly: true,
                      decoration: const InputDecoration(
                        labelText: 'Time',
                        prefixIcon: Icon(LucideIcons.clock),
                      ),
                      onTap: () async {
                        final TimeOfDay? picked = await showTimePicker(
                          context: context,
                          initialTime: selectedTime ?? TimeOfDay.now(),
                        );
                        if (picked != null) {
                          setModalState(() {
                            selectedTime = picked;
                            // Use context from StatefulBuilder for formatting
                            timeController.text = picked.format(context); 
                          });
                        }
                      },
                    ),
                    const SizedBox(height: 24),
                    ElevatedButton(
                      onPressed: () {
                        if(timeController.text.isEmpty) {
                          ScaffoldMessenger.of(modalContext).showSnackBar(
                            const SnackBar(content: Text('Please select a time.'), backgroundColor: Colors.red,),
                          );
                          return;
                        }
                        _addMedicine(
                          nameController.text,
                          dosageController.text,
                          timeController.text, // Pass the formatted time
                          modalContext,
                        );
                      },
                      child: const Text('Save Medicine'),
                    ),
                    const SizedBox(height: 20),
                  ],
                ),
              ),
            );
          },
        );
      },
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Maternal Health Hub'),
      ),
      body: StreamBuilder<QuerySnapshot>(
        stream: _medicineRef.orderBy('createdAt').snapshots(),
        builder: (context, snapshot) {
          if (snapshot.hasError) {
            return const Center(child: Text('Error loading medicines.'));
          }
          if (snapshot.connectionState == ConnectionState.waiting) {
            return const Center(child: CircularProgressIndicator());
          }
          if (snapshot.data!.docs.isEmpty) {
            return const Center(
              child: Text(
                'No medicines added yet.\nTap the + button to add one.',
                textAlign: TextAlign.center,
                style: TextStyle(color: Colors.grey),
              ),
            );
          }

          return ListView(
            padding: const EdgeInsets.all(8.0),
            children: snapshot.data!.docs.map((doc) {
              final data = doc.data() as Map<String, dynamic>;
              final bool isTaken = data['isTaken'] ?? false;
              
              // *** MODIFIED ***
              // Wrap list tile in Dismissible
              return Dismissible(
                key: Key(doc.id), 
                onDismissed: (direction) {
                  _deleteMedicine(doc);
                },
                background: Container(
                  color: Colors.red,
                  alignment: Alignment.centerRight,
                  padding: const EdgeInsets.symmetric(horizontal: 20.0),
                  child: const Icon(LucideIcons.trash2, color: Colors.white),
                ),
                child: Card(
                  child: ListTile(
                    contentPadding: const EdgeInsets.symmetric(vertical: 10, horizontal: 16),
                    title: Text(
                      data['name'],
                      style: TextStyle(
                        fontWeight: FontWeight.bold,
                        decoration: isTaken ? TextDecoration.lineThrough : null,
                      ),
                    ),
                    subtitle: Text(
                      '${data['dosage']} - ${data['time']}',
                      style: TextStyle(
                        decoration: isTaken ? TextDecoration.lineThrough : null,
                      ),
                    ),
                    trailing: Checkbox(
                      value: isTaken,
                      onChanged: (bool? value) {
                        _toggleIsTaken(doc);
                      },
                      activeColor: Colors.pink,
                    ),
                    onTap: () {
                      _toggleIsTaken(doc);
                    },
                  ),
                ),
              );
            }).toList(),
          );
        },
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: _showAddMedicineSheet,
        tooltip: 'Add Medicine',
        child: const Icon(LucideIcons.plus),
      ),
    );
  }
}

