// --- NutritionScreen ---
class NutritionScreen extends StatefulWidget {
  final Map<String, dynamic> userData;
  final User user;
  const NutritionScreen({super.key, required this.userData, required this.user});

  @override
  State<NutritionScreen> createState() => _NutritionScreenState();
}

class _NutritionScreenState extends State<NutritionScreen> {
  bool _isLoading = false;
  String? _aiResponse;
  String? _errorMessage;

  Future<void> _getNutritionTips() async {
    final String geminiApiKey = dotenv.env['GEMINI_API_KEY'] ?? 'YOUR_GEMINI_API_KEY_HERE';
    setState(() {
      _isLoading = true;
      _errorMessage = null;
      _aiResponse = null;
    });

    if (geminiApiKey == "YOUR_GEMINI_API_KEY_HERE" || geminiApiKey.isEmpty) {
       setState(() {
        _errorMessage = "API Key is not set. Please create a .env file with your Gemini API Key.";
        _isLoading = false;
      });
      return;
    }

    // Create a prompt from the user's profile data
    final prompt = """
    You are a helpful and caring nutritional assistant for maternal health.
    A user has provided the following health profile. Please provide 5-7 actionable,
    safe, and personalized nutritional tips based on this data.
    Address the user directly (e.g., "Based on your...").
    
    USER PROFILE:
    - Name: ${widget.userData['name']}
    - Estimated Due Date: ${widget.userData['dueDate']}
    - Medical History: ${widget.userData['medicalHistory']}
    - Current Conditions: ${widget.userData['currentConditions']}
    - Allergies: ${widget.userData['allergies']}
    
    Please provide your tips as a bulleted list.
    """;

    final url = Uri.parse(
        'https://generativelanguage.googleapis.com/v1beta/models/gemini-2.5-flash-preview-09-2025:generateContent?key=$geminiApiKey');

    try {
      final response = await http.post(
        url,
        headers: {'Content-Type': 'application/json'},
        body: jsonEncode({
          'contents': [
            {
              'parts': [
                {'text': prompt}
              ]
            }
          ]
        }),
      );

      if (response.statusCode == 200) {
        final data = jsonDecode(response.body);
        final text =
            data['candidates'][0]['content']['parts'][0]['text'] as String;
        setState(() {
          _aiResponse = text;
        });
      } else {
        final errorBody = jsonDecode(response.body);
        setState(() {
          _errorMessage =
              'Error from AI: ${errorBody['error']['message'] ?? 'Unknown error'}';
        });
      }
    } catch (e) {
      setState(() {
        _errorMessage = 'Failed to connect to AI service: ${e.toString()}';
      });
    } finally {
      setState(() {
        _isLoading = false;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Maternal Health Hub'),
      ),
      body: SingleChildScrollView(
        padding: const EdgeInsets.all(16.0),
        child: Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Text(
                'Personalized Nutrition',
                style: Theme.of(context).textTheme.headlineSmall,
              ),
              const SizedBox(height: 8),
              const Text(
                'Get AI-powered tips based on your profile.',
                textAlign: TextAlign.center,
                style: TextStyle(color: Colors.grey),
              ),
              const SizedBox(height: 32),
              ElevatedButton(
                onPressed: _isLoading ? null : _getNutritionTips,
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.amber[700],
                ),
                child: const Text('Get My Tips'),
              ),
              const SizedBox(height: 32),
              if (_isLoading)
                const CircularProgressIndicator(),
              if (_errorMessage != null)
                Text(
                  _errorMessage!,
                  style: const TextStyle(color: Colors.red),
                  textAlign: TextAlign.center,
                ),
              if (_aiResponse != null)
                // *** MODIFIED ***
                // Replaced SelectableText with MarkdownBody
                Container(
                  padding: const EdgeInsets.all(16.0),
                  decoration: BoxDecoration(
                    color: Colors.white,
                    borderRadius: BorderRadius.circular(12.0),
                    border: Border.all(color: Colors.grey[200]!),
                  ),
                  child: MarkdownBody( // Use MarkdownBody
                    data: _aiResponse!,
                    selectable: true,
                    styleSheet: MarkdownStyleSheet.fromTheme(Theme.of(context)).copyWith(
                      p: const TextStyle(fontSize: 16, height: 1.5),
                    ),
                  ),
                ),
            ],
          ),
        ),
      ),
    );
  }
}